                       <i className="fa fa-plus add-btn" title="Add Item" onClick={addItem}></i>
                    </div>

                    <div className="showItems">

                         {
                           items.map((elem)=>{
                             return(
                               <div className="eachItem" key={elem.id}>
                               <h3>{elem.name}</h3>
                               <div className="todo-btn">
                                 <i className="far fa-edit add-btn" title="Edit Item" onClick={()=>editItem(elem.id)}>E</i>
                                 <i className="far fa-trash-alt add-btn" title="Delete Item" onClick={()=>deleteItem(elem.id)}>D</i>
                               </div>


