            else{
                  setItems([...items, inputData]);
                  setInputData('')
            }
    }

    const deleteItem=(id)=>{
      const updatedItems = items.filter((elem, ind)=>{
                return ind != id;
      });
      setItems(updatedItems);
    }

    const removeAll=()=>{
      setItems([]);
    }


    const editItem=()=>{

    }


    return (
        <Fragment>
            <div className="main-div">
                 <div className="child-div">

                    <figure>
                         <figcaption>Add Your List Here</figcaption>
                    </figure>

                    <div className="addItems">

                       <input type="text" placeholder="Add Items..."
                         value = {inputData}
                         onChange={(e)=>{setInputData(e.target.value)}}>
                       </input>

                       <i className="fa fa-plus" title="Add Item" onClick={addItem}></i>
                    </div>

                    <div className="showItems">

                         {
                           items.map((elem, ind)=>{
                             return(
                               <div className="eachItem" key={ind}>
                               <h3>{elem}</h3>
                               <div className="todo-btn">
                                 <button className="far fa-edit add-btn" title="Edit Item" onClick={()=>editItem(ind)}>Edit</button>
                                 <i className="far fa-trash-alt add-btn" title="Delete Item" onClick={()=>deleteItem(ind)}>Delete</i>
                               </div>


                            </div>
                             )
                           })
                         }

                    </div>

                    <div className="showItems">
                       <button className="btn effect04" data-sm-link-text="Remove All" onClick={removeAll}><span>CHECK LIST</span></button>

                    </div>

                 </div>

            </div>
        </Fragment>
    )
}
